<script setup>
import { RouterLink } from 'vue-router';
import { onMounted } from 'vue';
import { gsap } from 'gsap';

const GST = (repeat, repeatDelay) => gsap.timeline({ repeat, repeatDelay });

onMounted(() => {
  // bg animation
  const bg = GST(-1, 3);

  bg.to('#bg', {
    transformOrigin: '50% 50%',
    scale: 1.2,
    duration: 3,
  }).to('#bg', {
    transformOrigin: '50% 50%',
    scale: 1,
    duration: 3,
  });

  // gears
  const gearSmall = GST(-1, 0);

  gearSmall.to('#gear-small', {
    transformOrigin: '50% 50%',
    rotate: 360,
    duration: 2,
    ease: 'none',
  });

  const gearBig = GST(-1, 0);

  gearBig.to('#gear-big', {
    transformOrigin: '50% 50%',
    rotate: -360,
    duration: 2,
    ease: 'none',
  });

  // planet
  const planet = GST(-1, 8);

  planet.to('#planet', {
    transformOrigin: '50% 50%',
    scale: 0.7,
    duration: 0.1,
    delay: 0,
  }).to('#planet', {
    transformOrigin: '50% 50%',
    scale: 1,
    duration: 0.1,
  });

  // envelop
  const envelop = GST(-1, 8);

  envelop.to('#envelop', {
    transformOrigin: '50% 50%',
    scale: 0.7,
    duration: 0.1,
    delay: 2,
  }).to('#envelop', {
    transformOrigin: '50% 50%',
    scale: 1,
    duration: 0.1,
  });

  // cart
  const cart = GST(-1, 8);

  cart.to('#cart', {
    transformOrigin: '50% 50%',
    scale: 0.7,
    duration: 0.1,
    delay: 4,
  }).to('#cart', {
    transformOrigin: '50% 50%',
    scale: 1,
    duration: 0.1,
  });

  // tools
  const tools = GST(-1, 8);

  tools.to('#tools', {
    transformOrigin: '50% 50%',
    scale: 0.7,
    duration: 0.1,
    delay: 6,
  }).to('#tools', {
    transformOrigin: '50% 50%',
    scale: 1,
    duration: 0.1,
  });
});
</script>
